const rows = $input.all();
const agentOutput = JSON.parse($('AI Agent').first().json.output);

const ID_ORIGEN = agentOutput.ID_ORIGEN;
const ID_DESTINO = agentOutput.ID_DESTINO;
const HORA_LOCAL = agentOutput.HORA_LOCAL;

// --- Funci√≥n auxiliar para convertir HH:MM a minutos ---
const toMin = (t) => {
  const [h, m] = t.split(':').map(Number);
  return h * 60 + m;
};

// --- Funci√≥n para formatear minutos en texto ---
const formatoTiempo = (min) => {
  if (!min) return "No disponible";
  const h = Math.floor(min / 60);
  const m = min % 60;
  if (h === 0) return `${m} min`;
  return `${h} h ${m} min`;
};

// --- Buscar las columnas correspondientes ---
const colOrigen = ID_ORIGEN.toString();
const colDestino = ID_DESTINO.toString();

// --- Obtener todos los horarios de esa columna ---
const horariosOrigen = rows.map(row => row.json[colOrigen]).filter(h => h);
const horariosDestino = rows.map(row => row.json[colDestino]).filter(h => h);

// --- Filtrar horarios v√°lidos (>= hora actual) ---
const horariosValidos = horariosOrigen
  .filter(horario => horario >= HORA_LOCAL)
  .slice(0, 4);

// --- Si no hay m√°s trenes disponibles ---
if (horariosValidos.length === 0) {
  return [{
    json: {
      origen: agentOutput.ORIGEN,
      destino: agentOutput.DESTINO,
      message: "No hay m√°s trenes disponibles hoy üöâ",
      horaActual: HORA_LOCAL
    }
  }];
}

// --- Construir lista de pr√≥ximos trenes con hora de arribo ---
const proximosTrenes = horariosValidos.map(hSalida => {
  const fila = rows.find(r => r.json[colOrigen] === hSalida);
  const hArribo = fila?.json[colDestino] || "No disponible";

  // Calcular tiempo estimado
  let tiempoMin = null;
  if (hArribo && /^\d{1,2}:\d{2}$/.test(hArribo)) {
    tiempoMin = toMin(hArribo) - toMin(hSalida);
  }

  return {
    salida: hSalida,
    arribo: hArribo,
    tiempoEstimado: tiempoMin > 0 ? formatoTiempo(tiempoMin) : "No disponible"
  };
});

// --- Generar mensaje de texto claro para el usuario ---
const mensajeViajes = proximosTrenes
  .map(t => `üïì Si tom√°s el tren de las ${t.salida}, lleg√°s a las ${t.arribo} (${t.tiempoEstimado}).`)
  .join("\n");

// --- Salida final ---
return [{
  json: {
    origen: agentOutput.ORIGEN,
    destino: agentOutput.DESTINO,
    horaActual: HORA_LOCAL,
    proximosTrenes,
    mensaje: mensajeViajes
  }
}];
